/*Soruto EJB 0.3.1*/var so=new Object;so.al=function(n){alert(n)},so.getId=function(n){return document.getElementById(n)},so.setVal=function(n,o){document.getElementById(n).value=o},so.getVal=function(n){return document.getElementById(n).value},so.coL=function(n){console.log(n)},so.getDT=function(){var n=new Date;return(n.getFullYear()+" "+(n.getMonth()+1)+" "+n.getDate()+" "+n.getHours()+" "+n.getMinutes()+" "+n.getSeconds()+" "+n.getDay()).split(" ")},so.sub=function(n){eval("document."+n+".submit();")},so.foc=function(i){eval('document.getElementById("'+i+'").focus();')},so.pMov=function(n){location.href=n},so.pUrl=function(){return location.href},so.pRl=function(){location.reload()},so.pBk=function(){history.back()},so.pFw=function(){history.forward()},so.ver=function(){return"0.3.1 beta"},so.setImgUrl=function(n,o){document.getElementById(n).src=o},so.pMovR=function(n){location.replace(n)},so.getDTT=function(){var n=new Date,o=n.getFullYear(),e=n.getMonth()+1,t=n.getDate(),c=n.getHours(),u=n.getMinutes(),i=n.getSeconds();return o+"/"+e+"/"+t+"("+["Sun","Mon","Tue","Wed","Thu","Fri","Sat"][n.getDay()]+") "+c+":"+u+":"+i},so.pTop=function(){scrollTo(0,0)},so.divIH=function(n,o){document.getElementById(n).innerHTML=o},so.divIHP=function(n,o){document.getElementById(n).innerHTML=document.getElementById(n).innerHTML+o},so.divIHT=function(n,o){document.getElementById(n).innerHTML=o+document.getElementById(n).innerHTML},so.cof=function(n){return window.confirm(n)},so.cofPMov=function(n,o){window.confirm(n)&&(location.href=o)},so.cofPON=function(n,o){window.confirm(n)&&window.open(o,"_blank")},so.coEr=function(n){console.error(n)};
